pub enum Flatten<Fut1, Fut2> {
    First {
        f: Fut1,
    },
    Second {
        f: Fut2,
    },
    Empty,
}

pub enum Map<Fut, F> {
    Incomplete {
        future: Fut,
        f: F,
    },
    Complete,
}

pub enum TryFlatten<Fut1, Fut2> {
    First {
        f: Fut1,
    },
    Second {
        f: Fut2,
    },
    Empty,
}

pub enum TryFlattenErr<Fut1, Fut2> {
    First {
        f: Fut1,
    },
    Second {
        f: Fut2,
    },
    Empty,
}
